#include<bits/stdc++.h>
using namespace std;
int step=0;
bool canReach(int i,int t,string &s,int min,int max,vector<vector<int> >& dp)
{
	cout<<"inside func"<<endl;
	cout<<"i is "<<i<<"t is "<<t<<endl;
	if(dp[i][t]!=-1)
	{
		cout<<"if 1"<<endl;
		if(dp[i][t]==1)
			return true;
		else
			return false;
	}
	if(i>=s.length() || t<0)
	{
		cout<<"if 2"<<endl;

		dp[i][t]=0;
		return false;
	}
	if(i==s.length()-1 || t==0)
	{
		cout<<"if 3"<<endl;

		dp[i][t]=1;
		return true;
	}
	dp[i][t]=false;
	cout<<"in"<<endl;
	if(i+min<s.size())
	{
		cout<<"if"<<endl;
		for(int j=i+min;j<=i+max;j++)
		{
	step++;

			cout<<"for"<<endl;				 
			if(s[j]=='0'){
				dp[i][t]= (bool)dp[i][t] || canReach(j,t-(j-i),s,min,max,dp);
				if(dp[i][t]==true)
					return true;	
			}
		}
	}
	cout<<"end"<<endl;
	return dp[i][t]; 
}



int main()
{
	#ifndef ONLINE_JUDGE
    freopen("/home/abhijeet/AMAN/Code/program_cpp/LeetCodeweekly/1TestArena/input.txt", "r", stdin);
    // freopen("output.txt", "w", stdout);
    #endif
	string s;
	cin>>s;
	int minJump,maxJump;
	cin>>minJump>>maxJump;
	bool ans;
	int n=s.length();
	cout<<"n is "<<n<<endl;
	vector<vector<int> > dp(n,vector<int> (n,-1));
	if(s[s.length()-1]=='1'){
		cout<<"here"<<endl;
		cout<<s.length()<<endl;
		ans=false;
	}
	else {
		cout<<"else"<<endl;
		cout<<"n is "<<n<<endl; 
		ans=canReach(0,n-1,s,minJump,maxJump,dp);
	}
	cout<<"Step is "<<step<<endl;
	cout<<"Ans is "<<ans<<endl;
}

